/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {

    `java-library`
    `maven-publish`
    id("java")
    id("com.gradleup.shadow") version "9.0.0-beta16" apply true
}

java {

    toolchain {
        languageVersion = JavaLanguageVersion.of(17)
    }
}

dependencies {
}

tasks {
    compileJava {
        sourceCompatibility = "17"
        targetCompatibility = "17"

    }

    jar {
        dependsOn(shadowJar)
        archiveFileName = "original-TNIL-Core-${project.version}.jar"
    }

    shadowJar {
        archiveFileName = "TNIL-Core-${project.version}.jar"
    }
}

publishing {
    publications {
        create<MavenPublication>("shadow") {
            groupId = property("tnil_group")!! as String?
            artifactId = "TNIL-Core"
            version = property("tnil_version")!! as String?

            from(components["shadow"])
        }
    }

    repositories {
        val mavenUrl: String? by project
        val mavenSnapshotUrl: String? by project

        (if(version.toString().endsWith("SNAPSHOT")) mavenSnapshotUrl else mavenUrl)?.let { url ->
            maven(url) {
                val mavenUsername: String? by project
                val mavenPassword: String? by project
                if(mavenUsername != null && mavenPassword != null) {
                    credentials {
                        username = mavenUsername
                        password = mavenPassword
                    }
                }
            }
        }
    }
}

description = "The New Item Library Core"