/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {

    `java-library`
    `maven-publish`
    id("com.github.johnrengelman.shadow") version "8.1.1" apply true
}

val paperApiVersion = property("io-papermc-paper-paper-api")!!

dependencies {
    api(project(":TNIL-Core"))
    api(project(":TNIL-BukkitBase"))
    compileOnly("io.papermc.paper:paper-api:$paperApiVersion")
}

java {

    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

tasks {
    compileJava {
        sourceCompatibility = "21"
        targetCompatibility = "21"
    }

    jar {
        dependsOn(shadowJar)
        archiveFileName = "original-TNIL-Paper-${project.version}.jar"
    }

    shadowJar {
        archiveFileName = "TNIL-Paper-${project.version}.jar"

        dependencies {
            include(dependency(":TNIL-BukkitBase"))
            include(dependency(":TNIL-Core"))
        }
    }
}

description = "The New Item Library Paper"
