/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    `java-library`
    `maven-publish`
    id("java")
    id("com.github.johnrengelman.shadow") version "8.1.1" apply true
}

val spongeApiVersion = property("org-spongepowered-spongeapi")!!

dependencies {
    implementation(project(":TNIL-Core"))
    compileOnly("org.spongepowered:spongeapi:$spongeApiVersion")
}

repositories {
    mavenCentral()
    maven {
        name = "sponge-repo"
        url = uri("https://repo.spongepowered.org/repository/maven-public/")
    }
}

java {

    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

tasks {
    compileJava {
        sourceCompatibility = "21"
        targetCompatibility = "21"
    }

    jar {
        dependsOn(shadowJar)
        archiveFileName = "original-TNIL-Sponge-${project.version}.jar"
    }

    shadowJar {
        archiveFileName = "TNIL-Sponge-${project.version}.jar"

        dependencies {
            include(dependency(":TNIL-Core"))
        }
    }
}

description = "The New Item Library Sponge"
